import React, { useState } from 'react'
import ReactDOM from 'react-dom'

const Statistics = ({good, neutral, bad}) => {
  let total = good + neutral + bad;
  let avg = (good - bad)/2;
  let percent = (good/total)*100;

  return (<div>
      <h2>statistics</h2>
    <ul>
      <li>Good:{good}</li>
      <li>Neutral:{neutral}</li>
      <li>Bad:{bad}</li>
      <li>Total feeback:{total}</li>
      <li>Avg Score:{avg}</li>
      <li>% positive:{percent}</li>
    </ul>
  </div>)
};

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  const handleGood = () => {
  setGood(good + 1);
  }
  const handleNeutral = () => {
    setNeutral(neutral + 1);
  }
  const handleBad = () => {
    setBad(bad + 1);
  }

  let feedbackSubmitted = good + neutral + bad !== 0;
  if(feedbackSubmitted) {

  return (
    <div>
      <h2>Give Feedback</h2>
      <button onClick={handleGood}>
        Good
      </button>
      <button onClick={handleNeutral}>
        Neutral
      </button>
      <button onClick={handleBad}>
        Bad
      </button>
      <Statistics good={good} neutral={neutral} bad={bad}/>
    </div>
  )
  } else {
    return <div>
      No feedback gathered
    </div>
  }
}

ReactDOM.render(<App />,
  document.getElementById('root')
)
